function OpenPopupMenu(menu) {
    if (menu === "LeftPanel") {
        menuLeft = true;
    }
    else {
        menuRight = true;
    }

    var command =
    {
        "Action": "display",
        "Compoent": menu,
        "Value": true
    };
    ClientEvents.publish("activateGeneric", command, false);
}

function ClosePopupMenu(menu) {
    if (menu === "LeftPanel") {
        menuLeft = false;
    }
    else {
        menuRight = false;
    }

    var command =
    {
        "Action": "Hide",
        "Compoent": menu,
        "Value": true
    };
    _publishCommand(command, "activateGeneric");

    if (menuLeft === false && menuRight === false) {
        command =
        {
            "Action": "HideIndex"
        };
        _publishCommand(command, "activateGeneric");

    }
}

