    catch (e) {
        setTimeout(function () {
            fw.func("JUMPSCREEN", "Initialisation");
        }, 3000);
    }
    var seleceted = Script.getState("SelectedName");
    var cardTemplate = Script.inheritScript("MenuTemplateRodent");
