    var topMenuTemplate = Script.inheritScript("Menu");
    _initializer(topMenuTemplate, "setTopHtml");
    var command =
    {
        "Action": "Hide",
        "Compoent": "RightPanel",
        "Value": true
    };
    _publishCommand(command, "activateGeneric");
    command =
    {
        "Action": "setVisible",
        "Compoent": "AddReport",
        "Value": "none",
    };
    _publishCommand(command, "activateGeneric");
    command =
    {
        "Action": "Hide",
        "Compoent": "LeftPanel",
        "Value": true
    };
    _publishCommand(command, "activateGeneric");
    command =
    {
        "Action": "setVal",
        "Compoent": "RightPanel",
        "Value": seleceted,
        "Id": "PageName",
    };
    _publishCommand(command, "SetTopAction");
    command =
    {
        "Action": "SetActiveDeactivate",
        "Compoent": "RightPanel",
        "Value": "dot|dot active",
        "Id": "Statuses|Active1|Active3",
    };
    _publishCommand(command, "SetTopAction");

    if (persistence.LastScreen == "Site Report List" || persistence.LastScreen == "Levels" || persistence.LocationId == null) {
        command =
        {
            "Action": "Change Generic Action",
            "Compoent": "status",
            "Value": "Landing Page",
        };
        _publishCommand(command, "SetTopAction");
    } else {
        command =
        {
            "Action": "Change Generic Action",
            "Compoent": "status",
            "Value": "RodentGrid",
        };
        _publishCommand(command, "SetTopAction");
    }
    command =
    {
        "Action": "HideIndex"
    };
