      };

      Database.readRecords(
        "rodent",
        "UserAccessRole",
        function (eventData) {
          deviceAction = parseInt(Object.values(eventData.value.data)[0][0]);
          //deviceAction = parseInt(Object.values(eventData.value.data)[0][1]);
          ClientEvents.publish("deviceAction", deviceAction);
          /// _initializer(topMenuTemplate, "setTopHtml");
          return;
        },
        filter
      );
