ClientEvents.subscribe("SaveTimeWindow", function (data) {
  Database.readRecords("rodent", "TimeWindow", function (evntData) {
    let collection = SensaCollection.load(evntData.value);
    let size = collection.size();
    let filteredWindow = collection
      .query((record, pk) => {
        if (
          record["DeviceId"] == data.value.id &&
          record["LocationId"] == data.value.locationId &&
          record["Status"] == 1
        ) {
          return true;
        }
      })
      .getFirst();
    if (!filteredWindow) {
      Database.readRecords("rodent", "Devices", function (evntData) {
        let devicesCollection = SensaCollection.load(evntData.value);
        let filteredDevice = devicesCollection
          .query((record, pk) => {
            if (record["Id"] == data.value.id) {
              return true;
            }
          })
          .getFirst();
        let dbVal = {};
        dbVal[size + 1] = {
          Id: size + 1,
          DeviceId: filteredDevice.Id,
          CompanyId: filteredDevice.CompanyId,
          BranchId: filteredDevice.BranchId,
          CustomerId: filteredDevice.CustomerId,
          SiteId: filteredDevice.SiteId,
          LocationId: filteredDevice.LocationId,
          StartDate: new Date().getTime(),
          EndDate: "31/12/2199 ",
          CreatedBy: fw.func("GETUSER"),
          CreatedOn: new Date().getTime(),
          ModifiedOn: new Date().getTime(),
          Status: "1",
          SystemCreated: "Y",
        };
        Database.saveRecordParam("rodent", "TimeWindow", dbVal);
      });
    }
  });
});
