ClientEvents.subscribe("ChildCardToggled", function (data) {
  // when child card is toggled, want to read the database for the device's current time window,
  // then send the time start date to the graphs, then change the instance for the graphs
  console.log(
    " DEVICE NUMBER FROM CHILD CARD TOGGLED IS: " + JSON.stringify(data.value)
  );
  var inputJson = data.value;
  Database.readRecords(
    "rodent",
    "TimeWindow",
    function (dbResponse) {
      var timeWindowCollection = SensaCollection.load(dbResponse.value);

      // calculate the end date
      var endDateObj = new Date();
      var endDateString =
        endDateObj.getUTCFullYear() +
        "-" +
        (endDateObj.getUTCMonth() + 1) +
        "-" +
        endDateObj.getUTCDate() +
        "," +
        endDateObj.getUTCHours() +
        ":" +
        endDateObj.getUTCMinutes() +
        ":" +
        endDateObj.getUTCSeconds();
      console.log("End Date String: " + endDateString);

      // try to get the start date out of the sensacollection
      var startDateUNIX;
      try {
        startDateUNIX = parseInt(
          timeWindowCollection.getColumn("StartDate")[0]
        );
      } catch (e) {
        console.log("No start date found");
        // set the start date and the end date to the current datetime.
        startDateUNIX = endDateObj.getTime();
        console.log(startDateUNIX);
      }
      // calculate the formatted start date
      var startDateObj = new Date(startDateUNIX);
      var startDateString =
        startDateObj.getUTCFullYear() +
        "-" +
        (startDateObj.getUTCMonth() + 1) +
        "-" +
        startDateObj.getUTCDate() +
        "," +
        startDateObj.getUTCHours() +
        ":" +
        startDateObj.getUTCMinutes() +
        ":" +
        startDateObj.getUTCSeconds();
      console.log("Start Date String: " + startDateString);

      console.log("Complete String: " + startDateString + "," + endDateString);

      ClientEvents.publish(
        "setGraphTimeWindow",
        startDateString + "," + endDateString
      );
      ClientEvents.publish(
        "setRodentTimeWindow",
        startDateString + "," + endDateString
      );
      ClientEvents.publish(
        "publishDeviceNumberToGraph",
        inputJson.deviceNumber
      );
    },
    {
      filter: "DeviceId=" + inputJson.deviceId + " AND Status=1",
    }
  );
});

