ClientEvents.subscribe("AddDevices", function (data) {
  const {
    deviceModelId,
    deviceQuantity,
    deviceName,
    locationId,
    nonSmartDeviceID,
  } = data;
  Database.readRecords("rodent", "Devices", function (eventData) {
    let collection = SensaCollection.load(eventData.value);
    const keys = Object.keys(collection.data);
    const id = parseInt(keys[keys.length - 1]) + 1;
    const prevDeviceNumber = nonSmartDeviceID.match(/\d+/)[0];
    let deviceNumber = nonSmartDeviceID;
    for (let i = 0; i < deviceQuantity; i++) {
      const dbVal = {};
      dbVal[id + i] = {
        Id: `${id + i}`,
        ModelId: deviceModelId,
        DeviceName: deviceName + "-" + deviceNumber,
        LocationId: locationId,
        DeviceNumber: deviceNumber,
        State: 0,
        BaitEnalbed: 1,
        SiteId: persistence.SiteId,
      };
      const nextDeviceNumber = parseInt(prevDeviceNumber) + 1 + i;
      deviceNumber = "N";
      const diff = prevDeviceNumber.length - nextDeviceNumber.toString().length;
      for (let j = 0; j < diff; j++) {
        deviceNumber += "0";
      }
      deviceNumber += nextDeviceNumber;
      Database.createRecords("rodent", "Devices", dbVal, function () {});
      if (i === deviceQuantity - 1) {
        var paramUpdate = [
          {
            Sql: "Update ApplicationSettings Set Value = $nextDeviceNumber Where Settings='Non Smart Device ID'",
            Data: [
              {
                Column: "$nextDeviceNumber",
                value: deviceNumber,
              },
            ],
          },
        ];
        Script.setState("deviceTableRowClicked", `${id}`);
        Database.cudParam(
          "rodent",
          "ApplicationSettings",
          paramUpdate,
          function () {
            LoadDevices(locationId);
          }
        );
      }
    }
  });
});

