function tableSortingFunction(a, b) {
  // Provide column index manually
  a = a.children[0].innerHTML.toUpperCase();
  b = b.children[0].innerHTML.toUpperCase();
  // necessary to make sure that the time is 4 digits. If the time was eg 8:00, it would originally output as 800, needs to be 0800
  var tempTimeA = a.split(" ")[1].split(":").join("");
  var tempTimeB = b.split(" ")[1].split(":").join("");
  if (tempTimeA.length == 3) {
    tempTimeA = "0" + tempTimeA;
  }
  if (tempTimeB.length == 3) {
    tempTimeB = "0" + tempTimeB;
  }

  a = parseInt(a.split(" ")[0].split("/").reverse().join("") + tempTimeA);
  b = parseInt(b.split(" ")[0].split("/").reverse().join("") + tempTimeB);
  return a > b ? -1 : a < b ? 1 : 0;
}

