function LoadHeatmapData(id) {
  Script.showWidget("heatMapTimeWindowLabel");
  Script.showWidget("heatMapTimeWindow");
  var paramsData;
  var query;
  if (id === null) {
    query =
      "Select D.Id, D.DeviceNumber, L.Name, L.Description From Devices as D join Locations as L on D.LocationId = L.Id where D.SiteId = $SiteId";
    paramsData = [
      {
        Column: "$SiteId",
        value: persistence.SiteId,
      },
    ];
  } else {
    paramsData = [
      {
        Column: "$SiteId",
        value: persistence.SiteId,
      },
      {
        Column: "$LocationId",
        value: persistence.LocationId,
      },
    ];
    query =
      "Select D.Id, D.DeviceNumber, L.Name, L.Description From Devices as D join Locations as L on D.LocationId = L.Id where D.SiteId = $SiteId and D.LocationId = $LocationId";
  }
  var sqlParam = [
    {
      Sql: query,
      Data: paramsData,
    },
  ];
  Database.ReadRecordsParam(
    "rodent",
    "Locations",
    sqlParam,
    function (currentPacket) {
      var devices = [];
      var startDate = new Date(new Date().getFullYear()).addDays(-150);
      for (var item in currentPacket.value.data) {
        var device = currentPacket.value.data[item][1];
        var locationName = currentPacket.value.data[item][2];
        var description = currentPacket.value.data[item][3];
        if (enableHeatMapTimeWindow) {
          const timeWindow = timeWindowDevices.find(
            (device) => device.deviceId == item
          );
          if (timeWindow) {
            devices.push({
              Device: device,
              LocationName: locationName,
              Description: description,
              startDate: new Date(parseInt(timeWindow.startDate)),
            });
          } else {
            devices.push({
              Device: device,
              LocationName: locationName,
              Description: description,
              startDate: startDate,
            });
          }
        } else {
          devices.push({
            Device: device,
            LocationName: locationName,
            Description: description,
            startDate: startDate,
          });
        }
      }

      var result = {
        startDate: startDate,
        endDate: new Date(),
        locationName: locationName,
        description: description,
        data: devices,
      };
      if (IsDesktop) ClientEvents.publish("clearheatmap", true, false);
      ClientEvents.publish("clear-table", true, false);
      ClientEvents.publish("subscribe-data", result, false);
    }
  );
}

