function GetUserAccess(userLevel) {
  var result;
  switch (userLevel) {
    case 1:
      result = {
        name: "Companies",
        id: undefined,
      };
      var command = {
        Action: "setVal",
        Compoent: "RightPanel",
        Value: "",
        Id: "PageName",
      };
      _publishCommand(command, "SetTopAction");
      break;
    case 2:
      result = {
        name: "Branches",
        id: persistence.BranchId,
      };
      parentId = persistence.CompanyId;
      break;
    case 3:
      result = {
        name: "Customers",
        id: persistence.CustomerId,
      };
      parentId = persistence.BranchId;
      break;
    case 4:
      if (IsDesktop) ShowReporting();
      result = {
        name: "Sites",
        id: persistence.SiteId,
      };
      parentId = persistence.CustomerId;
      break;
  }
  lastId = result.id;
  return result;
}

ClientEvents.subscribe("GenericActionMessage", function (data) {
  if (data === "GoBack|null") {
    if (IsDesktop) {
      Script.hideWidget("heatMapTimeWindowLabel");
      Script.hideWidget("heatMapTimeWindow");
    }
    ClientEvents.publish("clear-table", true, false);
    if (IsDesktop) ClientEvents.publish("clearheatmap", true, false);

    DeviceLevel = false;
    DeactivateSiteControls();
    var id;
    if (activeLevel === undefined) {
      ReturnToCustomers();

      return;
    }

    switch (activeLevel.Prev) {
      case "Companies":
        backHidden = true;
        HideBackButton();
        var command = {
          Action: "setVal",
          Compoent: "RightPanel",
          Value: "",
          Id: "PageName",
        };
        _publishCommand(command, "SetTopAction");
        //id = persistence.CompanyId;
        break;
      case "Branches":
        id = persistence.BranchId;
        parentId = persistence.CompanyId;
        if (IsDesktop) HideReporting();
        break;
      case "Customers":
        id = persistence.CustomerId;
        parentId = persistence.BranchId;
        if (IsDesktop) HideReporting();
        break;
      case "Sites":
        id = persistence.SiteId; //persistence.LocationId;
        parentId = persistence.CustomerId; //persistence.SiteId;
        ShowReporting();
        break;
      default:
        ReturnToCustomers();
        return;
    }
    lastId = id;
    activeLevel = levels[activeLevel.Prev];
    persistence.LevelAccess = activeLevel.LevelIndex;
    _UpdatePersistance(persistence);
    if (activeLevel.LevelIndex > 1) {
      ShowBackButton();
    } else {
      HideBackButton();
    }
    LoadHeaders(activeLevel.Header, id);
    GetParentName(parentId, activeLevel);
  } else {
    _handleGeneric(data);
  }
});

function filterCardsByType(query, type, mode) {
  let NameFilter = `Name like '${query}%'`;
  Database.readRecords(
    "rodent",
    type + "s",
    function (eventData) {
      let collection = SensaCollection.load(eventData.value);
      const items = Object.keys(collection.data).map((id) => ({
        id: id,
        name: collection.data[id][1],
      }));
      ClientEvents.publish("load-search-items", items, false);
    },
    {
      filter: mode == "Active" ? NameFilter + " And Status = 1" : NameFilter,
    }
  );
}

ClientEvents.subscribe("HandleGenericMessage", function (data) {
  let dataType = data.split("|")[0];
  switch (dataType) {
    case "SearchFor":
      if (data.split("|")[2] === "Cards") {
        ClientEvents.publish("filterCards", data.split("|")[1], false);
      } else {
        filterCardsByType(
          data.split("|")[1],
          data.split("|")[2],
          data.split("|")[3]
        );
      }
      break;
    case "AdjustSize":
      ClientEvents.publish("setPosition", data.split("|")[1], false);
      break;
    case "FilterFor":
      ClientEvents.publish(
        "filterCards",
        {
          value: data.split("|")[1],
        },
        false
      );
      break;
  }
});

ClientEvents.subscribe("EditCard", function (data) {
  ClientEvents.publish("EnableEdit", data.value, false);
});

function ReturnToCustomers() {
  lastId = persistence.CustomerId;
  parentId = persistence.BranchId;
  if (IsDesktop) HideReporting();

  activeLevel = levels["Customers"];
  // persistence.LevelAccess = activeLevel;
  _UpdatePersistance(persistence);

  LoadHeaders(
    {
      table: "Customers",
      columns: ["Id", "Name", "Address", "Status"],
      Subquery1: {
        column: "CustomerId",
        value: "Customers.Id",
      },
      Subquery2: {
        column: "Devices.CustomerId",
        value: "Customers.Id",
      },
      Icon: "CustomersIcon",
    },
    lastId
  );
  GetParentName(parentId, activeLevel);
}

function GetParentName(id, level) {
  var compoundQeury = [
    {
      TableName: level.Prev, // Specify the primary table used to construct the join request
      Join: 1, // Join type 1 - Inner Join, 2 - Outer Join, 3 - Left join, 4 - Right Join.
      Columns: ["Name"],
      Filters: [
        {
          Column: "Id", //Column that will be applied (don't include table name will be resolved if its contained within the reference table.
          ParamName: "Id", // Parameter name, must be unique and should not contain $ as its resolved by the mapper.
          Value: id, // Value that you want to pass for comparison (any object)
        },
      ], // Specify the list of columns to take from the reference table.
    },
  ];
  //   if (level.Next === "Device Level") {
  //     // there is no table called device level
  //     return;
  //   }
  Database.readCompound("rodent", level.Next, compoundQeury, function (packet) {
    var current;
    for (var val in packet.value.data) {
      current = packet.value.data[val];
    }
    var command = {
      Action: "setVal",
      Compoent: "RightPanel",
      Value: current[0],
      Id: "PageName",
    };
    _publishCommand(command, "SetTopAction");
  });
}

