ClientEvents.subscribe("branchDeletePressed", function (eventData) {
    Database.readRecords("Rodent", "Devices", async function (deviceValues) {
        // Check if any active devices within branch
        if (model.Status == 1 && Object.keys(deviceValues.value.data).length > 0) {
            // Do not allow branch to be deactivated if devices are present
            Client.alert("There are active devices currently at a location within the hierarchy", "Delete not Permitted");
            return;
        }

        let res = await Client.confirm("Do you want to toggle this branch, and all customers, sites and locations within this branch?",
            (model.Status == 1 ? "Deactivate" : "Activate") + " all levels?",
            { confirmText: "Yes", cancelText: "No" });
        if (!res) {
            return;
        }

        var dbVal = {};
        dbVal["Id"] =
        {
            "Id": model.Id,
            "Name": model.Name,
            "Address": model.Address,
            "RelativeId": persistence.CompanyId,
            "Country": model.Country,
            "Status": model.Status == 1 ? "0" : "1",
            "UpdatedBy": fw.func("GETUSER"),
            "UpdatedOn": new Date().getTime(),
            "AddressLine1": model.AddressLine1,
            "PostCode": model.PostCode === undefined ? "" : model.PostCode,
            "City": model.City === undefined ? "" : model.City,
            "State": model.State === undefined ? "" : model.State,
            "County": model.County === undefined ? "" : model.County
        };
        // Toggle activate the branch
        Database.saveRecordParam("Rodent", "Branches", dbVal, async function (result) {
            if (model.Status != 1) {
                // Prompt user for activating all lower levels
                let res = await Client.confirm("Do you want to activate all customers, sites, and locations within this branch?",
                    "Activate all levels?",
                    { confirmText: "Yes", cancelText: "No" });
                if (!res) {
                    fw.func("JUMPSCREEN", Caller);
                    return;
                }
            }
            // Toggle activate customers of the branch
            toggleActivateCustomers(model.Id);
        });
    }, { filter: `BranchId='${model.Id}' AND Status='${model.Status}'` });
});

function toggleActivateCustomers(branchId) {
    Database.readRecords("Rodent", "Customers", async function (customerValues) {
        var customersRemaining = Object.keys(customerValues.value.data).length;
        if (customersRemaining) {
            customerValues.value.forEach((record, pk) => {
                record.Status = model.Status == 1 ? "0" : "1";
                var dbVal = {};
                dbVal["Id"] = record;
                Database.saveRecordParam("Rodent", "Customers", dbVal, function (result) {
                    customersRemaining--;
                    toggleActivateSites(record.Id, customersRemaining);
                });
            });
        }
    }, { filter: `RelativeId='${branchId}' AND Status='${model.Status}'` });
}

function toggleActivateSites(customerId, customersRemaining) {
    Database.readRecords("Rodent", "Sites", function (siteValues) {
        console.log("Toggling sites for customer " + customerId);
        var sitesRemaining = Object.keys(siteValues.value.data).length;
        if (sitesRemaining) {
            siteValues.value.forEach((record, pk) => {
                record.Status = model.Status == 1 ? "0" : "1";
                var dbVal = {};
                dbVal["Id"] = record;
                Database.saveRecordParam("Rodent", "Sites", dbVal, async function (result) {
                    sitesRemaining--;
                    toggleActivateLocations(record.Id, customersRemaining, sitesRemaining);
                });
            });
        }
    }, { filter: `RelativeId='${customerId}' AND Status='${model.Status}'` });
}

function toggleActivateLocations(siteId, customersRemaining, sitesRemaining) {
    Database.readRecords("Rodent", "Locations", function (locationValues) {
        console.log("Toggling locaitons for site " + siteId);
        var locationsRemaining = Object.keys(locationValues.value.data).length;
        if (locationsRemaining) {
            locationValues.value.forEach((record, pk) => {
                record.Status = model.Status == 1 ? "0" : "1";
                var dbVal = {};
                dbVal["Id"] = record;
                locationsRemaining--;
                Database.saveRecordParam("Rodent", "Locations", dbVal);
            });
            if (!locationsRemaining && !sitesRemaining && !customersRemaining) {
                fw.func("JUMPSCREEN", Caller);
            }
        }
    }, { filter: `RelativeId='${siteId}' AND Status='${model.Status}'` });
}

function LoadModel(id) {
    var getAdvisory =
        [{
            "TableName": "Branches", // Specify the primary table used to construct the join request
            "Join": 1, // Join type 1 - Inner Join, 2 - Outer Join, 3 - Left join, 4 - Right Join.
            "Columns":
                [
                    "Id",
                    "Name",
                    "Address",
                    "CreatedOn",
                    "UpdatedOn",
                    "Status",
                    "Country",
                    "Latitude",
                    "Longitude",
                    "PostCode",
                    "City",
                    "AddressLine1",
                    "City",
                    "State",
                    "County"

                ], // Specify the list of columns to take from the reference table.
            "Filters": // Filter options in case they are needed.
                [
                    {
                        "Column": "Id",
                        "ParamName": "branchId",
                        "Value": id
                    }
                ]
        }];
    Database.readCompound("rodent", "Branches", getAdvisory, function (currentData) {

        var branchData;
        for (var item in currentData.value.data) {
            branchData = currentData.value.data[item];
        }
        model = {};
        model.Id = branchData[0];
        model.Name = branchData[1];
        model.Address = branchData[2];
        model.CreatedOn = branchData[3];
        model.UpdatedOn = branchData[4];
        model.Status = branchData[5];
        model.Country = branchData[6];
        model.Latitude = branchData[7];
        model.Longitude = branchData[8];
        model.PostCode = branchData[9];
        model.AddressLine1 = branchData[10];
        model.City = branchData[11];
        model.State = branchData[12];
        model.County = branchData[13];

        if (model.Status == 1) {
            // Branch is enabled, show deactivate button
            ClientEvents.publish("branchDeleteSetStyle", { Property: "background-color", PropertyValue: "#d12d1b" }, false);
            ClientEvents.publish("branchActiveSetText", "Deactivate Branch");
        } else {
            // Branch is disabled, show activate button
            ClientEvents.publish("branchDeleteSetStyle", { Property: "background-color", PropertyValue: "#6ec3f6ff" }, false);
            ClientEvents.publish("branchActiveSetText", "Activate Branch");
        }

        if (model.Name !== undefined) {
            var devAddress = (typeof model.Address === "string") ? model.Address.split(",") : "";
            if (devAddress.length === 6) {
                var addressList = devAddress.filter(function (value, index, arr) {
                    return index !== 3;
                });
                model.Address = addressList.join(", ");
            }
            ClientEvents.publish("name-value", model.Name, false);
        }

        if (model.Address !== undefined) {
            ClientEvents.publish("Address-value", model.Address, false);
        }

        if (model.CreatedOn !== undefined && model.CreatedOn !== "") {

            var date = new Date(parseInt(model.CreatedOn)).toLocaleString();
            var d = date.split(",");
            var d0 = d[0].split("/");
            d0[2] = d0[2] - 2000;
            d0 = d0.join("/");
            ClientEvents.publish("created-date", d0, false);
        }
        else {
            var date = new Date().toLocaleString();
            var d = date.split(",");
            var d0 = d[0].split("/");
            d0[2] = d0[2] - 2000;
            d0 = d0.join("/");
            ClientEvents.publish("created-date", d0, false);
        }


        if (model.CreatedOn !== undefined && model.CreatedOn !== "") {

            var date = new Date(parseInt(model.CreatedOn));
            var format = formatDate(date, "dd/MM/yyyy");
            ClientEvents.publish("created-date", format, false);
        }

        if (model.UpdatedOn !== undefined && model.UpdatedOn !== "") {

            var date = new Date(parseInt(model.UpdatedOn));
            var format = formatDate(date, "dd/MM/yyyy");

            ClientEvents.publish("created-date", format, false);
            ClientEvents.publish("date-label", "Updated On", false);
            ClientEvents.publish("date-label-style", "color:white;", false);

        }

        // var countryCode = branchData[6];
        // for (var country in countryList) {
        //     var currentCountry = countryList[country];
        //     if (countryCode === currentCountry.code) {
        //         ClientEvents.publish("set-country", currentCountry.name, false);
        //     }
        // }
        var countryCode = model.Country;
        for (var country in countryList) {
            var currentCountry = countryList[country];

            if (countryCode.length == 2) {

                if (countryCode === currentCountry.code) {
                    ClientEvents.publish("set-country", currentCountry.name, false);
                    ClientEvents.publish("setCountry", currentCountry.code, false);
                    break;
                }

            } else if (countryCode === currentCountry.name) {
                ClientEvents.publish("set-country", countryCode, false);
                ClientEvents.publish("setCountry", currentCountry.code, false);
                break;
            }
        }


    });
}

