function getLongAddressObject(object) {
    let address = {};
    const address_components = object;
    address_components.forEach(element => {
        address[element.types[0]] = element.long_name;
    });
    return address;
}

ClientEvents.subscribe("Address-changed", function (data) {

    model.Address = "";
    var addressData = data.value.address_components;


    model.Address = addressData[0].long_name + ", " + addressData[1].short_name + ", " + addressData[2].long_name + ", " + addressData[4].long_name + ", " + addressData[5].long_name;

    //model.AddressLine1 = addressData[0].long_name + ", " + addressData[1].short_name + ", " + addressData[2].long_name;
    model.AddressLine1 = addressData[0].long_name + ", " + addressData[1].short_name;// + ", " + addressData[2].long_name;

    // model.State = addressData[4].long_name;
    // model.City = addressData[3].long_name;
    // model.Country = addressData[5].long_name;
    // model.Zip = addressData[6].long_name;
    // model.County = addressData[4].long_name;
    var completeAddress = getLongAddressObject(addressData);
    model.Latitude = data.value.geometry.location.lat();
    model.Longitude = data.value.geometry.location.lng();
    /*
        var findResult = function (results, name) {
            var result = _.find(results, function (obj) {
                return obj.types[0] == name && obj.types[1] == "political";
            });
            return result ? result.short_name : null;
        }; */

    model.State = completeAddress.administrative_area_level_1;
    model.City = completeAddress.locality;
    model.Country = completeAddress.country;
    model.Zip = completeAddress.postal_code; //postal_code
    var county = completeAddress.administrative_area_level_2;
    model.County = county == undefined || county == null ? "" : county;
    //"administrative_area_level_2"

    if (addressData.length > 7) {
        model.Address = model.Address + ", " + model.Country;
        //model.AddressLine1 = model.AddressLine1 + ", " + addressData[2].long_name;
    }

    var addressList = model.Address.split(',');
    model.Address = [...new Set(addressList)].toString();


    //console.log("Address is " + JSON.stringify(data.value, null, 4));
    //debugger;

});

