ClientEvents.subscribe("inactivateCustomer", function (eventData) {
    Database.readRecords("Rodent", "Devices", async function (deviceValues) {
        //console.log(deviceValues);
        // Check if any active devices with customer
        if (model.Status == 1 && Object.keys(deviceValues.value.data).length > 0) {
            // Do not allow customer to be deactivated if devices are present
            Client.alert("There are active devices currently at a location within the hierarchy", "Delete not Permitted");
            return;
        }
        let res = await Client.confirm("Do you want to toggle this customer, and all sites and locations within this customer?",
            (model.Status == 1 ? "Deactivate" : "Activate") + " all levels?",
            { confirmText: "Yes", cancelText: "No" });
        if (!res) {
            return;
        }
        var dbVal = {};
        var form = Script.getFormByKey("custormer");
        var Classification = form.Classification;
        //model.Classification
        dbVal["Id"] =
        {
            "Id": model.Id,
            "Name": model.Name,
            "Address": model.Address,
            "AddressLine1": model.AddressLine1,
            "RelativeId": persistence.BranchId,
            "PrimaryContactName": model.PrimaryContactName === undefined ? "" : model.PrimaryContactName,
            "PrimaryContactEmail": model.PrimaryContactEmail === undefined ? "" : model.PrimaryContactEmail,
            "PrimaryContactMobile": model.PrimaryContactMobile === undefined ? "" : model.PrimaryContactMobile,
            "Classification": Classification === undefined ? "" : Classification,
            "Country": model.Country === undefined ? "" : model.Country,
            "Zip": model.Zip === undefined ? "" : model.Zip,
            "City": model.City === undefined ? "" : model.City,
            "State": model.State === undefined ? "" : model.State,
            "Latitude": model.Latitude === undefined ? "" : model.Latitude,
            "Longitude": model.Longitude === undefined ? "" : model.Longitude,
            "Status": model.Status == 1 ? "0" : "1",
            "UpdatedBy": fw.func("GETUSER"),
            "UpdatedOn": new Date().getTime()
        };
        // Toggle activate the branch
        Database.saveRecordParam("Rodent", "Customers", dbVal, async function (result) {
            if (model.Status != 1) {
                // Prompt user for activating all lower levels
                let res = await Client.confirm("Do you want to activate all sites, and locations for this customer?",
                    "Activate all levels?",
                    { confirmText: "Yes", cancelText: "No" });
                if (!res) {
                    fw.func("JUMPSCREEN", Caller);
                    return;
                }
            }
            // Toggle activate sites of the customer
            toggleActivateSites(model.Id);
        });
    }, { filter: `CustomerId='${model.Id}' AND Status='${model.Status}'` });
});


function toggleActivateSites(customerId) {
    Database.readRecords("Rodent", "Sites", function (siteValues) {
        var sitesRemaining = Object.keys(siteValues.value.data).length;
        if (sitesRemaining) {
            siteValues.value.forEach((record, pk) => {
                record.Status = model.Status == 1 ? "0" : "1";
                var dbVal = {};
                dbVal["Id"] = record;
                Database.saveRecordParam("Rodent", "Sites", dbVal, function (result) {
                    sitesRemaining--;
                    toggleActivateLocations(record.Id, sitesRemaining);
                });
            });
        }
    }, { filter: `RelativeId='${customerId}' AND Status='${model.Status}'` });
}

function toggleActivateLocations(siteId, sitesRemaining) {
    Database.readRecords("Rodent", "Locations", function (locationValues) {
        //console.log("Toggling locaitons for site " + siteId);
        var locationsRemaining = Object.keys(locationValues.value.data).length;
        if (locationsRemaining) {
            locationValues.value.forEach((record, pk) => {
                record.Status = model.Status == 1 ? "0" : "1";
                var dbVal = {};
                dbVal["Id"] = record;
                locationsRemaining--;
                Database.saveRecordParam("Rodent", "Locations", dbVal);
            });
        }
        //console.log(`LOCATIONS ${locationsRemaining}, SITES ${sitesRemaining}`)
        if (!locationsRemaining && !sitesRemaining) {
            fw.func("JUMPSCREEN", Caller);
        }
    }, { filter: `RelativeId='${siteId}' AND Status='${model.Status}'` });
}

function LoadModel(id) {
    var getAdvisory =
        [{
            "TableName": "Customers", // Specify the primary table used to construct the join request
            "Join": 1, // Join type 1 - Inner Join, 2 - Outer Join, 3 - Left join, 4 - Right Join.
            "Columns":
                [
                    "Id",
                    "Name",
                    "Address",
                    "PrimaryContactName",
                    "PrimaryContactEmail",
                    "PrimaryContactMobile",
                    "Classification",
                    "CreatedOn",
                    "UpdatedOn",
                    "Status",
                    "Country",
                    "Zip",
                    "City",
                    "State",
                    "AddressLine1",
                    "Latitude",
                    "Longitude",
                    "County"

                ], // Specify the list of columns to take from the reference table.
            "Filters": // Filter options in case they are needed.
                [
                    {
                        "Column": "Id",
                        "ParamName": "branchId",
                        "Value": id
                    }
                ]
        }];
    Database.readCompound("rodent", "Customers", getAdvisory, function (currentData) {
        var branchData;
        for (var item in currentData.value.data) {
            branchData = currentData.value.data[item];
        }
        model = {};
        model.Id = branchData[0];
        model.Name = branchData[1];
        model.Address = branchData[2];
        model.PrimaryContactName = branchData[3];
        model.PrimaryContactEmail = branchData[4];
        model.PrimaryContactMobile = branchData[5];
        model.Classification = branchData[6];
        model.CreatedOn = branchData[7];
        model.UpdatedOn = branchData[8];
        model.Status = branchData[9];
        model.Country = branchData[10];
        model.Zip = branchData[11];
        model.City = branchData[12];
        model.State = branchData[13];
        model.AddressLine1 = branchData[14];
        model.Latitude = branchData[15];
        model.Longitude = branchData[16];
        model.County = branchData[17];

        if (model.Status == 1) {
            // Customer is enabled, show deactivate button
            ClientEvents.publish("setInactiveCustomerStyle", { Property: "background-color", PropertyValue: "#d12d1b" }, false);
            ClientEvents.publish("setActiveCustomerText", "Deactivate Customer");
        } else {
            // Customer is disabled, show activate button
            ClientEvents.publish("setInactiveCustomerStyle", { Property: "background-color", PropertyValue: "#6ec3f6ff" }, false);
            ClientEvents.publish("setActiveCustomerText", "Activate Customer");
        }


        if (model.Name !== undefined) {
            ClientEvents.publish("name-value", model.Name, false);
        }

        if (model.Address !== undefined) {
            var devAddress = (typeof model.Address === "string") ? model.Address.split(",") : "";
            if (devAddress.length === 6) {
                var addressList = devAddress.filter(function (value, index, arr) {
                    return index !== 3;
                });
                model.Address = addressList.join(", ");
            }
            ClientEvents.publish("Address-value", model.Address, false);
        }

        if (model.PrimaryContactName !== undefined) {
            ClientEvents.publish("primary-name-value", model.PrimaryContactName, false);
        }

        if (model.PrimaryContactEmail !== undefined) {
            ClientEvents.publish("primary-email-value", model.PrimaryContactEmail, false);
        }

        if (model.PrimaryContactMobile !== undefined && model.PrimaryContactMobile !== "") {
            ClientEvents.publish("mobile-value", model.PrimaryContactMobile, false);
        }

        if (model.Classification !== undefined) {
            ClientEvents.publish("set-classification", model.Classification, false);
        }


        if (model.CreatedOn !== undefined && model.UpdatedOn !== "") {

            var date = new Date(parseInt(model.CreatedOn));
            var format = formatDate(date, "dd/MM/yyyy");
            ClientEvents.publish("created-date", format, false);
            ClientEvents.publish("setDateColor", "color:white;", false);

        }

        if (model.UpdatedOn !== undefined && model.UpdatedOn !== "") {

            var date = new Date(parseInt(model.UpdatedOn));
            var format = formatDate(date, "dd/MM/yyyy");

            ClientEvents.publish("created-date", format, false);
            ClientEvents.publish("date-label", "Updated On", false);
            ClientEvents.publish("setDateColor", "color:white;", false);

        }

        var countryCode = model.Country;
        // ClientEvents.publish("set-country", model.Country, false);
        for (var country in countryList) {
            var currentCountry = countryList[country];

            if (countryCode.length == 2) {

                if (countryCode === currentCountry.code) {
                    ClientEvents.publish("set-country", currentCountry.name, false);
                    ClientEvents.publish("setCountry", currentCountry.code, false);
                    break;
                }

            } else if (countryCode === currentCountry.name) {
                ClientEvents.publish("set-country", countryCode, false);
                ClientEvents.publish("setCountry", currentCountry.code, false);
                break;
            }
        }
    });
}


function validate() {

    var result = true;
    if (model.Name === "") {
        Client.alert("Name is required!");
        return false;
    }

    if (model.PrimaryContactEmail === "Invalid Input" || model.PrimaryContactEmail === "" && NameChanged) {
        Client.alert("Primary Contact Email is required!");
        return false;
    }
    if (model.PrimaryContactMobile === "" && NameChanged) {
        Client.alert("Mobile is required!");
        return false;
    }

    return result;
}


function formatDate(date, format, utc) {
    var MMMM = ["\x00", "January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    var MMM = ["\x01", "Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
    var dddd = ["\x02", "Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
    var ddd = ["\x03", "Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
    function ii(i, len) { var s = i + ""; len = len || 2; while (s.length < len) s = "0" + s; return s; }

    var y = utc ? date.getUTCFullYear() : date.getFullYear();
    format = format.replace(/(^|[^\\])yyyy+/g, "$1" + y);
    format = format.replace(/(^|[^\\])yy/g, "$1" + y.toString().substr(2, 2));
    format = format.replace(/(^|[^\\])y/g, "$1" + y);

    var M = (utc ? date.getUTCMonth() : date.getMonth()) + 1;
    format = format.replace(/(^|[^\\])MMMM+/g, "$1" + MMMM[0]);
    format = format.replace(/(^|[^\\])MMM/g, "$1" + MMM[0]);
    format = format.replace(/(^|[^\\])MM/g, "$1" + ii(M));
    format = format.replace(/(^|[^\\])M/g, "$1" + M);

    var d = utc ? date.getUTCDate() : date.getDate();
    format = format.replace(/(^|[^\\])dddd+/g, "$1" + dddd[0]);
    format = format.replace(/(^|[^\\])ddd/g, "$1" + ddd[0]);
    format = format.replace(/(^|[^\\])dd/g, "$1" + ii(d));
    format = format.replace(/(^|[^\\])d/g, "$1" + d);

    var H = utc ? date.getUTCHours() : date.getHours();
    format = format.replace(/(^|[^\\])HH+/g, "$1" + ii(H));
    format = format.replace(/(^|[^\\])H/g, "$1" + H);

    var h = H > 12 ? H - 12 : H == 0 ? 12 : H;
    format = format.replace(/(^|[^\\])hh+/g, "$1" + ii(h));
    format = format.replace(/(^|[^\\])h/g, "$1" + h);

    var m = utc ? date.getUTCMinutes() : date.getMinutes();
    format = format.replace(/(^|[^\\])mm+/g, "$1" + ii(m));
    format = format.replace(/(^|[^\\])m/g, "$1" + m);

    var s = utc ? date.getUTCSeconds() : date.getSeconds();
    format = format.replace(/(^|[^\\])ss+/g, "$1" + ii(s));
    format = format.replace(/(^|[^\\])s/g, "$1" + s);

    var f = utc ? date.getUTCMilliseconds() : date.getMilliseconds();
    format = format.replace(/(^|[^\\])fff+/g, "$1" + ii(f, 3));
    f = Math.round(f / 10);
    format = format.replace(/(^|[^\\])ff/g, "$1" + ii(f));
    f = Math.round(f / 10);
    format = format.replace(/(^|[^\\])f/g, "$1" + f);

    var T = H < 12 ? "AM" : "PM";
    format = format.replace(/(^|[^\\])TT+/g, "$1" + T);
    format = format.replace(/(^|[^\\])T/g, "$1" + T.charAt(0));

    var t = T.toLowerCase();
    format = format.replace(/(^|[^\\])tt+/g, "$1" + t);
    format = format.replace(/(^|[^\\])t/g, "$1" + t.charAt(0));

    var tz = -date.getTimezoneOffset();
    var K = utc || !tz ? "Z" : tz > 0 ? "+" : "-";
    if (!utc) {
        tz = Math.abs(tz);
        var tzHrs = Math.floor(tz / 60);
        var tzMin = tz % 60;
        K += ii(tzHrs) + ":" + ii(tzMin);
    }
    format = format.replace(/(^|[^\\])K/g, "$1" + K);

    var day = (utc ? date.getUTCDay() : date.getDay()) + 1;
    format = format.replace(new RegExp(dddd[0], "g"), dddd[day]);
    format = format.replace(new RegExp(ddd[0], "g"), ddd[day]);

    format = format.replace(new RegExp(MMMM[0], "g"), MMMM[M]);
    format = format.replace(new RegExp(MMM[0], "g"), MMM[M]);

    format = format.replace(/\\(.)/g, "$1");

    return format;
}


function LoadDefaultCountry() {
    var getBranch =
        [{
            "TableName": "Branches", // Specify the primary table used to construct the join request
            "Join": 1, // Join type 1 - Inner Join, 2 - Outer Join, 3 - Left join, 4 - Right Join.
            "Columns":
                [
                    "Country"

                ], // Specify the list of columns to take from the reference table.
            "Filters": // Filter options in case they are needed.
                [
                    {
                        "Column": "Id",
                        "ParamName": "branchId",
                        "Value": persistence.BranchId
                    }
                ]
        }];
    Database.readCompound("rodent", "Branches", getBranch, function (currentData) {
        //debugger
        for (var item in currentData.value.data) {
            var current = currentData.value.data[item];
            var countryCode = current[0];
            for (var country in countryList) {
                var currentCountry = countryList[country];
                if (countryCode === currentCountry.code) {
                    ClientEvents.publish("set-country", currentCountry.name, false);
                    ClientEvents.publish("setCountry", currentCountry.code, false);
                    model.Country = currentCountry.name;

                }
            }
        }
    });
}

