function populateFormData() {
    if (model.Status == 1) {
        // Site is enabled, show deactivate button
        ClientEvents.publish("setSiteActivateStyle", { Property: "background-color", PropertyValue: "#d12d1b" }, false);
        ClientEvents.publish("setSiteActiveText", "Deactivate Site");
    } else {
        // Site is disabled, show activate button
        ClientEvents.publish("setSiteActivateStyle", { Property: "background-color", PropertyValue: "#6ec3f6" }, false);
        ClientEvents.publish("setSiteActiveText", "Activate Site");
    }

    if (model.Name !== undefined) {
        ClientEvents.publish("name-value", model.Name, false);
    }

    if (model.Address !== undefined) {
        var devAddress = (typeof model.Address === "string") ? model.Address.split(",") : "";
        if (devAddress.length === 6) {
            var addressList = devAddress.filter(function (value, index, arr) {
                return index !== 3;
            });
            model.Address = addressList.join(", ");
        }
        ClientEvents.publish("Address-value", model.Address, false);
    }

    if (model.PrimaryContactName !== undefined) {
        ClientEvents.publish("primary-name-value", model.PrimaryContactName, false);
    }

    if (model.PrimaryContactEmail !== undefined) {
        ClientEvents.publish("primary-email-value", model.PrimaryContactEmail, false);
    }

    if (model.PrimaryContactMobile !== undefined) {
        ClientEvents.publish("mobile-value", model.PrimaryContactMobile, false);
    }

    if (model.AlertOwner !== undefined) {
        ClientEvents.publish("set-alertOwner", model.AlertOwner, false);
    }

    if (model.Alert !== undefined) {
        ClientEvents.publish("toggle-switch", model.Alert, false);
    }
    console.log("PRIORITY: " + SelectedPriority);
    if (SelectedPriority !== undefined) {
        console.log("inside priority");
        switch (SelectedPriority) {
            case "1":
                SelectType("urgent", true);
                break;
            case "2":
                SelectType("important", true);
                break;
            case "3":
                SelectType("minor", true);
                break;
        }
    }

    if (model.CreatedOn !== undefined) {

        var date = new Date(parseInt(model.CreatedOn));
        var format = formatDate(date, "dd/MM/yyyy");
        ClientEvents.publish("created-date", format, false);
    }

    if (model.UpdatedOn !== undefined && model.UpdatedOn != "") {

        var date = new Date(parseInt(model.UpdatedOn));
        var format = formatDate(date, "dd/MM/yyyy");

        ClientEvents.publish("created-date", format, false);
        ClientEvents.publish("date-label", "Updated On", false);
        ClientEvents.publish("date-label-style", "color:white;", false);

    }

    // var countryCode = model.country;
    // for (var country in countryList) {
    //     var currentCountry = countryList[country];
    //     if (countryCode === currentCountry.code) {
    //         ClientEvents.publish("set-country", currentCountry.name, false);
    //     }
    // }
    var countryCode = model.Country;
    console.log("Country code  thrue in " + model.Country);
    // ClientEvents.publish("set-country", model.Country, false);
    for (var country in countryList) {
        var currentCountry = countryList[country];

        if (countryCode.length == 2) {

            if (countryCode === currentCountry.code) {
                ClientEvents.publish("set-country", currentCountry.name, false);
                ClientEvents.publish("setCountry", currentCountry.code, false);
                console.log("Country code " + currentCountry.code + " name " + currentCountry.name);
                break;
            }

        } else if (countryCode === currentCountry.name) {
            ClientEvents.publish("set-country", countryCode, false);
            ClientEvents.publish("setCountry", currentCountry.code, false);
            console.log("Country code  in " + currentCountry.code + " name " + countryCode);
            break;
        }
    }

    if (model.AlertGroup !== undefined) {
        console.log(thresholdGroups.get(String(model.AlertGroup)).text);
        alertGroupDrop.receiveValue(thresholdGroups.get(String(model.AlertGroup)).text);
    }
}



 function addAllertUsers() {

     var alerSiteOwner = Script.getWidget("alerSiteOwner");
     var user = Client.getUser();
     var isPrime = 1;
     var userFilter =
         [{
             "TableName": "UserRoles", // Specify the primary table used to construct the join request
             "Join": 1, // Join type 1 - Inner Join, 2 - Outer Join, 3 - Left join, 4 - Right Join.
             "Columns":
                 [
                     "User",
                     "CompanyId"


                 ], // Specify the list of columns to take from the reference table.
             "Filters": // Filter options in case they are needed.
                 [
                     {
                         "Column": "IsPrimary",
                         "ParamName": "IsPrimary",
                         "Value": isPrime
                     }
                 ]
         }];

     var users;
     Database.readCompound("rodent", "UserRoles", userFilter, (eventData) => {

         var userSensa = SensaCollection.load(eventData.value);
         CompanyId = userSensa.getColumn("CompanyId");
         users = userSensa.getColumn("User")
         var userNameData = [];

         var userIndex = users.indexOf(user);

         userCopamyId = CompanyId[userIndex];

         for (var i = 0; i < CompanyId.length; i++) {
             if (CompanyId[i] == userCopamyId) {
                 //var index = 
                 userNameData.push(users[i]);
             }
         }
         userNameData = [...new Set(userNameData)];
         //debugger;
         Database.readRecords("Directory", "users", (data) => {
             var sensaData = SensaCollection.load(data.value);

             var aliasData = [];

             var username = sensaData.getColumn("username");
             var alias = sensaData.getColumn("alias");
             var status = sensaData.getColumn("status");

             for (var i = 0; i < username.length; i++) {
                 for (var j = 0; j < userNameData.length; j++) {
                     if (username[i] == userNameData[j] && status[i] == 1) {
                         aliasData.push(alias[i]);
                     }
                 }
             }
             alerSiteOwner.receiveList(aliasData.sort());

             chooseHowFormIsLoaded();
             //debugger;
         })

     });
 }

