ClientEvents.subscribe("locationActivePressed", function (eventData) {
    Database.readRecords("Rodent", "Devices", async function (deviceValues) {
        // Check if any active devices with location
        if (model.Status == 1 && Object.keys(deviceValues.value.data).length > 0) {
            // Do not allow location to be deactivated if devices are present
            Client.alert("There are active devices in this location", "Delete not Permitted");
            return;
        }

        let res = await Client.confirm("Do you want to toggle this location?",
            (model.Status == 1 ? "Deactivate" : "Activate") + " location",
            { confirmText: "Yes", cancelText: "No" });
        if (!res) {
            return;
        }

        var dbVal = {};
        dbVal["Id"] =
        {
            "Id": model.Id,
            "Name": model.Name,
            "Description": model.Address,
            "RelativeId": persistence.SiteId,
            "AlertOwner": model.AlertOwner,
            "Alert": model.Alert,
            "Priority": SelectedPriority,
            "alertgroup": model.AlertGroup,
            "Status": model.Status == 1 ? "0" : "1",
            "UpdatedBy": fw.func("GETUSER"),
            "UpdatedOn": new Date().getTime()
        };
        // Toggle the site
        Database.saveRecordParam("Rodent", "Locations", dbVal, function (result) {
            fw.func("JUMPSCREEN", Caller);
        });
    }, { filter: `LocationId='${model.Id}' AND Status='${model.Status}'` });
});

function chooseHowFormIsLoaded() {

    var date = new Date();
    var format = formatDate(date, "dd/MM/yyyy");
    ClientEvents.publish("created-date", format, false);

    // check if we are coming back from the recipients screen
    console.log(Script.getState("recipients"));
    if (Script.getState("recipients") == "true") {
        // populate the various forms with the previous data.
        console.log("returning from recipient screen, populating the form data");
        try {
            model = Script.getState("location-model");
            SelectedPriority = Script.getState("location-priority");
            console.log("Model:\n" + JSON.stringify(model,null,4));
            console.log("SelectedPriority:\n" + SelectedPriority);
        } catch (error) {
            console.log("error loading previous model data: " + error.toString());
        }

        loadAlertGroups();

    } else if (maitnanceId !== null && maitnanceId !== undefined && maitnanceId !== "0") {
        console.log("loading existing location")
        LoadModel(maitnanceId);
    }
    else {
        console.log("location doesn't exist, filling default values")
        Database.readLastPrimaryKey("Rodent", "Locations", function (eventData) {
            model.Id = parseInt(eventData.value) + 1;
        });
        model.Name = "";
        model.Address = "";
        model.Alert = "0"
        SelectedPriority = "3";
        model.AlertOwner = fw.func("GETUSER");
        populateFormData();
    }
}

function loadProcedure() {
    // need to load company id, then threshold group table.
    // callback hell though. Then loads the alert groups, then decides how to populate the forms.
    addAllertUsers();
}

function saveRecipients() {
    // save the recipients for a new record, as saved in the statestore
    var recipientsRecord = Script.getState("recipients-record");
    var returningFromRecipients = Script.getState("recipients");
    if (recipientsRecord == "" || returningFromRecipients != "true") {
        console.log("No recipients record found, not saving recipients");
        return;
    } 
    // set it to false now
    Script.setState("recipients", "false");

    console.log("Recipients found, saving");
    recipientsRecord = JSON.parse(recipientsRecord);
    // modify the record so the location id is set to the newly created location id
    var recordKeys = Object.keys(recipientsRecord);
    recipientsRecord[recordKeys[0]].locationid = model.Id;
    console.log(JSON.stringify(recipientsRecord,null,4));

    Database.saveRecord("rodent","alertrecipients", recipientsRecord, function(dbResponse) {
        console.log("recipient record saved");
    });
}

function loadAlertGroups() {
    // read database for alert groups given company id
    // populate the dropdown with the alert group items
    var filter = {filter: `companyid='${userCopamyId}' AND priority='${SelectedPriority}'`, columns: "id, name"};
    console.log("filter" + JSON.stringify(filter));
    Database.readRecords("rodent", "thresholdgroup", function(dbResponse) {
        try {
            thresholdGroups = SensaCollection.load(dbResponse.value);
            thresholdGroups.add({name: "no group", id: "0"});
            thresholdGroups.renameColumn("name","text");
            thresholdGroups.renameColumn("id","value");
            thresholdGroups = thresholdGroups.filter(['text','value']);
        } catch (e) {
            // error with loading the database values, assume no group
            thresholdGroups = new SensaCollection(["text", "value"], "text", {});
            thresholdGroups.add({text: "no group", value: "0"});
        }
        console.log(JSON.stringify(thresholdGroups,null,4));
        alertGroupDrop.receiveTextValues(thresholdGroups);

        // get the id from it on load
        //model.AlertGroup = thresholdGroups.getFirst().value;
        console.log(JSON.stringify(model,null,4));
        populateFormData();

    }, filter);
}


function populateFormData() {
    console.log("Populating: " + JSON.stringify(model,null,4));
    // send data to all the forms
    if (model.Status == 1) {
        // Site is enabled, show deactivate button
        ClientEvents.publish("locationActiveSetStyle", { Property: "background-color", PropertyValue: "#d12d1b" }, false);
        ClientEvents.publish("locationActiveSetText", "Deactivate Location");
    } else {
        // Site is disabled, show activate button
        ClientEvents.publish("locationActiveSetStyle", { Property: "background-color", PropertyValue: "#6ec3f6ff" }, false);
        ClientEvents.publish("locationActiveSetText", "Activate Location");
    }

    if (model.Name !== undefined) {
        ClientEvents.publish("name-value", model.Name, false);
    }

    if (model.Address !== undefined) {
        ClientEvents.publish("Address-value", model.Address, false);
    }

    if (model.AlertOwner !== undefined) {
        ClientEvents.publish("set-alertOwner", model.AlertOwner, false);
    }

    if (model.Alert !== undefined) {
        ClientEvents.publish("toggle-switch", model.Alert, false);
    }

    if (SelectedPriority !== undefined) {
        switch (SelectedPriority) {
            case "1":
                SelectType("urgent", true);
                break;
            case "2":
                SelectType("important", true);
                break;
            case "3":
                SelectType("minor", true);
                break;
        }
    }

    if (model.CreatedOn !== undefined) {

        var date = new Date(parseInt(model.CreatedOn));
        var format = formatDate(date, "dd/MM/yyyy");
        ClientEvents.publish("created-date", format, false);
    }

    if (model.UpdatedOn !== undefined && model.UpdatedOn !== "") {

        var date = new Date(parseInt(model.UpdatedOn));
        var format = formatDate(date, "dd/MM/yyyy");

        ClientEvents.publish("created-date", format, false);
        ClientEvents.publish("date-label", "Updated On", false);
        ClientEvents.publish("date-label-style", "color:white;", false);

    }

    console.log(JSON.stringify(thresholdGroups,null,4));
    console.log(typeof model.AlertGroup + " " + model.AlertGroup);
    if (model.AlertGroup !== undefined && model.AlertGroup !== "") {
        console.log("alert group exists");
        console.log(thresholdGroups.get(String(model.AlertGroup)).text);
        alertGroupDrop.receiveValue(thresholdGroups.get(String(model.AlertGroup)).text);
    } else {
        console.log("alert group doesn't exist");
        model.AlertGroup = "0";
        console.log(JSON.stringify(thresholdGroups,null,4));
        alertGroupDrop.receiveValue(thresholdGroups.get(String(model.AlertGroup)).text);
    }

}

function LoadModel(id) {
    var getAdvisory =
        [{
            "TableName": "Locations", // Specify the primary table used to construct the join request
            "Join": 1, // Join type 1 - Inner Join, 2 - Outer Join, 3 - Left join, 4 - Right Join.
            "Columns":
                [
                    "Id",
                    "Name",
                    "Description",
                    "AlertOwner",
                    "Alert",
                    "Priority",
                    "CreatedOn",
                    "UpdatedOn",
                    "Status",
                    "alertgroup",


                ], // Specify the list of columns to take from the reference table.
            "Filters": // Filter options in case they are needed.
                [
                    {
                        "Column": "Id",
                        "ParamName": "branchId",
                        "Value": id
                    }
                ]
        }];
    Database.readCompound("rodent", "Locations", getAdvisory, function (currentData) {

        var branchData;
        for (var item in currentData.value.data) {
            branchData = currentData.value.data[item];
        }
        model = {};
        model.Id = branchData[0];
        model.Name = branchData[1];
        model.Address = branchData[2];
        model.AlertOwner = branchData[3];
        model.Alert = branchData[4];
        SelectedPriority = branchData[5];
        model.CreatedOn = branchData[6];
        model.UpdatedOn = branchData[7];
        model.Status = branchData[8];
        model.AlertGroup = branchData[9];
        loadAlertGroups();
    });
}

