function enableProvisioning() {
    var user = Client.getUser();
    //console.log("User is " + user);
    var selectUser = [
        {
            TableName: "users", // Specify the primary table used to construct the join request
            Join: 1, // Join type 1 - Inner Join, 2 - Outer Join, 3 - Left join, 4 - Right Join.
            Columns: [
                //"username",
                "accessrole",
            ], // Specify the list of columns to take from the reference table.
            // Filter options in case they are needed.
            Filters: [
                {
                    Column: "username", //Column that will be applied (don't include table name will be resolved if its contained within the reference table.
                    ParamName: "username", // Parameter name, must be unique and should not contain $ as its resolved by the mapper.
                    Value: user, // Value that you want to pass for comparison (any object)
                },
            ],
        },
    ];

    Database.readCompound("Directory", "users", selectUser, (eventData) => {
        var accessRoleId = Object.values(eventData.value.data)[0][0];
        //console.log("Access role value is " + accessRoleId);
        // var errorEnable = {};
        if (accessRoleId === "" || accessRoleId == 0 || accessRoleId == null) {
            return;
        }
        var filter = {
            columns: "Feature02",
            filter: "Id=" + accessRoleId,
        };

        Database.readRecords(
            "rodent",
            "UserAccessRole",
            function (eventData) {
                //console.log("Data read from user access role " + JSON.stringify(eventData.value, null, 4));
                var provisioningAccess = parseInt(
                    Object.values(eventData.value.data)[0][0]
                );

                if (provisioningAccess === 1) {
                    formCotrol = 1;
                }
            },
            filter
        );
    });
}

