                        //console.log("DEvo persistance data is " + JSON.stringify(persistence, null, 4));
                        var devCompanyId = parseInt(values[4]);
                        var devBranchId = parseInt(values[5]);
                        var devSiteId = parseInt(values[1]);
                        var devCustomerId = parseInt(values[3]);
                        var devLocationId = parseInt(values[2]);

                        var ActivationStatus = current[7];
                        if (ActivationStatus != 4) {
                            Client.alert(
                                "This device does not exist"
                            );
                            return;
                        }



                        //var values = Object.values(eventData.value.data);
                        var companyId =
                            typeof persistence.CompanyId === "string"
                                ? parseInt(persistence.CompanyId)
                                : null;
                        var branchId =
                            typeof persistence.BranchId === "string"
                                ? parseInt(persistence.BranchId)
                                : null;
                        var siteId =
                            typeof persistence.SiteId === "string"
                                ? parseInt(persistence.SiteId)
                                : null;
                        var locationId =
                            typeof persistence.LocationId === "string"
                                ? parseInt(persistence.LocationId)
                                : null;

                        //var customerID = typeof persistence.CustomerId === "string" ? parseInt(persistence.CustomerId) : null;
                        var LastScreen =
                            typeof persistence.LastScreen === "string"
                                ? persistence.LastScreen
                                : "Levels";

                        var dbVal = {};
                        dbVal[current[0]] = {
                            Id: current[0],
                            SiteId: persistence.SiteId,
                            LocationId: persistence.LocationId,
                            CustomerId: persistence.CustomerId,
                            CompanyId: persistence.CompanyId,
                            BranchId: persistence.BranchId,
                            StationId: null,
                        };
                        //  console.log("DEvo persistance data is " + JSON.stringify(persistence, null, 4));
                        //  console.log(`DEvo device values are Devcomp ${devCompanyId} comp ${companyId} devC ${devCustomerId} and cus ${customerID}
                        //  devBr ${devBranchId} br ${branchId} devSit ${devSiteId} side is ${siteId} devL ${devLocationId} ${locationId}`);
                        if (devCompanyId === companyId) {
                            //|| devCustomerId === customerID

                            if (devBranchId === branchId) {
                                // same site or deleted device
                                if (devSiteId === siteId || devSiteId === 0) {
                                    if (locationId !== devLocationId || devLocationId === 0) {
                                        Database.updateRecord(
                                            "Rodent",
                                            "Devices",
                                            dbVal,
                                            function (data) {
                                                Script.setState("barcodeId", current[0], true);
                                                _UpdatePersistance(persistence);
                                                fw.func("JUMPSCREEN", LastScreen); // LastScreen, "Device Level"
                                            }
                                        );
                                    } else {
                                        fw.func("JUMPSCREEN", LastScreen); //
                                    }
                                } else {
                                    Client.alert(
                                        `This device is installed at a different site and cannot be moved to a different one.\nYou need to remove it first`
                                    );
                                    fw.func("JUMPSCREEN", LastScreen);
                                }
                            } else {
                                // different branch

                                if (devSiteId === 0) {
                                    var option = { confirmText: "Confirm", cancelText: "Cancel" };
                                    Client.confirm(
                                        "Are you sure you want to move this device between branches?",
                                        "Move device between branches",
                                        option
                                    ).then((eventData) => {
                                        //console.log("Return data is  is " + JSON.stringify(eventData, null, 4));

                                        if (!eventData) {
                                            fw.func("JUMPSCREEN", LastScreen);
                                        } else {
                                            Database.updateRecord(
                                                "Rodent",
                                                "Devices",
                                                dbVal,
                                                function (data) {
                                                    Script.setState("barcodeId", current[0], true);
                                                    _UpdatePersistance(persistence);
                                                    fw.func("JUMPSCREEN", LastScreen); // LastScreen, "Device Level"
                                                }
                                            );
                                        }
                                    });
                                } else {
                                    Client.alert("This device is at a diffent branch");
                                    fw.func("JUMPSCREEN", LastScreen);
                                }
                            }
                        } else {
                            Client.alert("The device does not exist");
                            fw.func("JUMPSCREEN", LastScreen); // LastScreen, "Device Level"
                        }

                        // Database.updateRecord("Rodent", "Devices", dbVal, function (data) {

                        //     Script.setState("barcodeId", current[0], true);
                        //     _UpdatePersistance(persistence);
                        //     fw.func("JUMPSCREEN", "Device Level");
                        // });
                    }
                }
            );
        } else {
            Client.alert(
                "Device is not known to the system please check Device ID or rescan"

            );
            return;
        }
    } else {
        if (DeviceId !== undefined) {
