function GetDevice(id, model) {
    var getDevice = [
        {
            TableName: model, // Specify the primary table used to construct the join request
            Join: 1, // Join type 1 - Inner Join, 2 - Outer Join, 3 - Left join, 4 - Right Join.
            Columns: ["serialnum", "name", "url", "imagename", "make"], // Specify the list of columns to take from the reference table.
            // Filter options in case they are needed.
            Filters: [
                {
                    Column: "name",
                    ParamName: "branchId",
                    Value: id,
                },
            ],
        },
    ];
    Database.readCompound(
        "pestco",
        "rodent_sensor",
        getDevice,
        function (currentData) {
            var current;
            for (var item in currentData.value.data) {
                current = currentData.value.data[item];
            }
            var LastScreen =
                typeof persistence.LastScreen === "string"
                    ? persistence.LastScreen
                    : "Levels";
            //

            if (current === undefined) {
                Client.alert(
                    "Device is not known to the system please check Device ID rescan or enter"
                );
                return;
            } else {
                var deviceNumber = DeviceId.device;
                var compoundQeury = [
                    {
                        TableName: "Devices", // Specify the primary table used to construct the join request
                        Join: 1, // Join type 1 - Inner Join, 2 - Outer Join, 3 - Left join, 4 - Right Join.
                        Columns: ["Id","DeviceName"],
                        Filters: [
                            {
                                Column: "DeviceNumber", //Column that will be applied (don't include table name will be resolved if its contained within the reference table.
                                ParamName: "DeviceNumber", // Parameter name, must be unique and should not contain $ as its resolved by the mapper.
                                Value: deviceNumber, // Value that you want to pass for comparison (any object)
                            },
                        ], // Specify the list of columns to take from the reference table.
                    },
                ];
                if (formCotrol === 0) {
                    Client.alert(
                        "You don't have adequate permission to provision this device"
                    );
                    fw.func("JUMPSCREEN", LastScreen);
                    return;
                }

                if (isFromAddDevice !== "true") {
                    // isFromAddDevice modeDevice !== "mode1"
                    //console.log("line 453 " + typeof isFromAddDevice);
                    Client.alert("This device is already provisioned!");
                    //fw.func("JUMPSCREEN", LastScreen);
                    return;
                }
                var findDev = 0;
                for (var id in scanDeviceData.data) {
                    if (scanDeviceData.data[id][21] == deviceNumber && scanDeviceData.data[id][92] == 3) {
                        findDev = 1;
                    }
                }

                if (findDev == 0) {
                    Client.alert(
                        "This device is not known to the system"
                    );
                    return;

                }
                try {
                    Database.readCompound(
                        "rodent",
                        "Devices",
                        compoundQeury,
                        function (packet) {
                            var devName;
                            //console.log("line 1221 : " + JSON.stringify(packet, null, 4));
                            for (var val in packet.value.data) {
                                devName = packet.value.data[val];
                            }

                            //debugger;
                            var devId = devName[0];
                            devName = devName[1];
                            console.log("Packet " + JSON.stringify(packet, null, 4));
                            console.log("Packet devName " + JSON.stringify(devName, null, 4));
                            console.log("Packet devId " + JSON.stringify(devId, null, 4));
                            DeviceId.model =
                                typeof selectedModel === "string"
                                    ? selectedModel
                                    : DeviceId.model;
                            current.imagename = "../images/Device.PNG";
                            current.modelName = model;
                            Script.setState("DeviceExists", current);
                            Script.setState("devModelId", modelId);
                            Script.setState("devModel", DeviceId.model);
                            Script.setState("device", DeviceId.device);
                            Script.setState("devName", devName);
                            Script.setState("devID", devId);
                            fw.func("JUMPSCREEN", "Provisioning Step 2");
                        }
                    );
                } catch (error) {
                    Client.alert("This device does not exist");
                    return;
                }
            }
        }
    );
