function ScanDevice(code) {
    if (isFromAddDevice != null) {
        // isFromAddDevice  modeDevice !== "mode2"

        //console.log("line 492 " + typeof isFromAddDevice);
        Client.alert("This device does not exist");
        //fw.func("JUMPSCREEN", LastScreen);
        return;
    }

    var selectAdvisories = [
        {
            TableName: "Devices", // Specify the primary table used to construct the join request
            Join: 1, // Join type 1 - Inner Join, 2 - Outer Join, 3 - Left join, 4 - Right Join.
            Columns: [
                "Id",
                "SiteId",
                "LocationId",
                "CustomerId",
                "CompanyId",
                "BranchId",
                "ActivationStatus"
            ], // Specify the list of columns to take from the reference table.
            // Filter options in case they are needed.
            Filters: [
                {
                    Column: "BarcodeId", //Column that will be applied (don't include table name will be resolved if its contained within the reference table.
                    ParamName: "BarcodeId", // Parameter name, must be unique and should not contain $ as its resolved by the mapper.
                    Value: code, // Value that you want to pass for comparison (any object)
                },
            ],
        },
    ];
    Database.readCompound(
        "rodent",
        "Devices",
        selectAdvisories,
        function (eventData) {
            var values = Object.values(eventData.value.data)[0];
            //console.log("DEvo VAlue is " + JSON.stringify(values, null, 4) + " type id " + typeof values);
            //console.log("DEvo persistance data is " + JSON.stringify(persistence, null, 4));

            var LastScreen =
                typeof persistence.LastScreen === "string"
                    ? persistence.LastScreen
                    : "Levels";
            //

            var current;
            for (var item in eventData.value.data) {
                current = eventData.value.data[item];
            }
            if (current === undefined) {
                Client.alert(
                    "Device is not known to the system please check Device ID or rescan"
                );
                //fw.func("JUMPSCREEN", LastScreen); //LastScreen
                return;
            } else {

                var ActivationStatus = current[6];
                if (ActivationStatus != 4) {
                    Client.alert(
                        "This device does not exist"
                    );
                    return;
                }
                var selectAdvisories = [
                    {
                        TableName: "Locations", // Specify the primary table used to construct the join request
                        Join: 1, // Join type 1 - Inner Join, 2 - Outer Join, 3 - Left join, 4 - Right Join.
                        Columns: ["Id"], // Specify the list of columns to take from the reference table.
                        // Filter options in case they are needed.
                        Filters: [
                            {
                                Column: "RelativeId", //Column that will be applied (don't include table name will be resolved if its contained within the reference table.
                                ParamName: "AreaId", // Parameter name, must be unique and should not contain $ as its resolved by the mapper.
                                Value: current[1], // Value that you want to pass for comparison (any object)
                            },
                        ],
                    },
                ];

                Database.readCompound(
                    "rodent",
                    "Locations",
                    selectAdvisories,
                    function (currentData) {
                        var selectedSites = [];
                        var areas;
                        for (var item in currentData.value.data) {
                            areas = currentData.value.data[item];
                            selectedSites.push(areas[0]);
                        }

                        if (current !== undefined) {
                            ClientEvents.publish("detection-end", "");

                            persistence.SiteId = current[1];
                            persistence.LocationId = current[2];
                            persistence.CompanyId = current[4];
                            persistence.CustomerId = current[3];
                            persistence.LastScreen = "Device Level";

                            Script.setState("SelectedCustomer", persistence.CustomerId);
                            Script.setState("SelectedArea", persistence.SiteId);

                            Script.setState("selectedSites", selectedSites);

                            Script.setState("selectedSite", persistence.LocationId);
                            Script.setState("getSite", persistence.SiteId);
                            Script.setState("rodent-persistance", persistence, true);
                            Script.setState("barcodeId", current[0], true);

                            _UpdatePersistance(persistence);
                            fw.func("JUMPSCREEN", "Device Level"); //LastScreen
                        }
                    }
                );
                // if(devBranchId === )
            }
        }
    );
}

