 function toggleActivateSites(companyId) {
     Database.readRecords("Rodent", "Branches", function (siteValues) {
         var sitesRemaining = Object.keys(siteValues.value.data).length;
         if (sitesRemaining) {
             siteValues.value.forEach((record, pk) => {
                 record.Status = model.Status == 1 ? "0" : "1";
                 var dbVal = {};
                 dbVal["Id"] = record;
                 Database.saveRecordParam("Rodent", "Branches", dbVal, function (result) {
                     sitesRemaining--;
                     toggleActivateCustomers(record.Id, sitesRemaining);
                 });
             });
         }
     }, { filter: `RelativeId='${companyId}' AND Status='${model.Status}'` });
 }


 function toggleActivateCustomers(branchId) {
