 function saveAndReturn() {
     // construct db save from the selected users collection.
     var jsonValue = {};
     // save value is a stringified json, email is key and alert is value
     currentSelectedUsers.forEach( function(row,pk) {
         jsonValue[pk] = row.Alert;
     });
     console.log(JSON.stringify(jsonValue,null,4));

     var dbVal = {};
     // first need to check if a record already exists with the selected attributes:
     // companyid, locationid, siteid
     // if a record does exist, use the id for it. Otherwise use the last primary key.
     var uniqueFilter;
     if (isSiteLevel) {
         uniqueFilter = {filter: "companyid="+companyId+" AND siteid="+siteId +" AND locationid IS null"};
     } else {
         uniqueFilter = {filter: "companyid="+companyId+" AND siteid="+siteId +" AND locationid="+locationId};
     }
     console.log("Unique filter:\n" + JSON.stringify(uniqueFilter,null,4));
     var id;
     var recordExists = false;
     Database.readRecords("rodent", "alertrecipients", function(uniqueData) {
         console.log("TYPEOF: " + typeof uniqueData.value.getFirst());
         console.log(JSON.stringify(uniqueData.value.getFirst()))
         if (uniqueData.value.getFirst() == undefined || uniqueData.value.getFirst() == null){
             console.log("no record already exists");
         } else {
             console.log("record already exists, update");
             recordExists = true;
             id = uniqueData.value.getFirst().id;
             console.log("record id: " + id);
         }

         Database.readLastPrimaryKey("rodent", "alertrecipients", function(eventData) {
             var lastPrimaryKey = parseInt(eventData.value);
             if (recordExists){
                 id = String(id);
             } else if (isNaN(lastPrimaryKey)) {
                 id = "1";
             } else {
                 id = String(lastPrimaryKey+1);
             }

             dbVal[id] = {id: id,
                          companyid: companyId,
                          siteid: siteId,
                          locationid: locationId,
                          recipients: JSON.stringify(jsonValue)
                         };
             console.log(JSON.stringify(dbVal,null,4));

             Script.setState("recipients-record", JSON.stringify(dbVal));
            //  Database.saveRecord("rodent","alertrecipients", dbVal, function(dbResponse) {
            //      console.log("record saved");
            if (isSiteLevel){
                Client.jumpToScreen("Site Form");
            } else {
                Client.jumpToScreen("Location Form");
            }
            //  });
         });
     },uniqueFilter);
 }

