 function loadSelectedUsers() {
     console.log("Loading selected users");
     var filter;
     if (isSiteLevel) {
         filter = {filter: "companyid="+companyId+" AND siteid="+siteId+" AND locationid is null"};
     } else {
         filter = {filter: "companyid="+companyId+" AND siteid="+siteId+" AND locationid="+locationId};
     }
     console.log("Filter:\n" + JSON.stringify(filter,null,4));
     Database.readRecords("rodent", "alertrecipients", function(dbResponse) {
         console.log(JSON.stringify(dbResponse,null,4));
         var collection = SensaCollection.load(dbResponse.value);
         var jsonValues;
         try {
            jsonValues = JSON.parse(collection.getFirst().recipients);
            console.log("Record found for company id: " + companyId + ", site id: " + siteId + ", location id: " + locationId);
         } catch (error) {
             console.log("No current record exists for company id: " + companyId + ", site id: " + siteId + ", location id: " + locationId);
             return;
         }

         console.log("JSON VALUES:\n" + JSON.stringify(jsonValues),null,4);
         Object.keys(jsonValues).forEach(function(key){
             if (jsonValues[key] == "1") {
                 currentSelectedUsers.add({"username": key, "name":"temp", "Email Alert": "1", "SMS Alert": "0", "Alert": "1"});
             } else if (jsonValues[key] == "2") {
                 currentSelectedUsers.add({"username": key, "name":"temp", "Email Alert": "0", "SMS Alert": "1", "Alert": "2"});
             } else {
                 currentSelectedUsers.add({"username": key, "name":"temp", "Email Alert": "1", "SMS Alert": "1", "Alert": "3"});
             }
         });
         currentSelectedUsers.forEach( function(row,pk) {
             // grab the alias from the other collection
             var name = allUsersFromCompanyCollection.get(pk).name;
             currentSelectedUsers.set({"username":pk,"name": name});
         });
         selectedUsersTable.receiveValue(currentSelectedUsers);
         sortSelectedUsersTable();
     }, filter);
 }

