 function getCompanyIdAndFillAllUsersTable() {
    var user = Client.getUser();
    var isPrime = 1;
    var userFilter =
        [{
            "TableName": "UserRoles", // Specify the primary table used to construct the join request
            "Join": 1, // Join type 1 - Inner Join, 2 - Outer Join, 3 - Left join, 4 - Right Join.
            "Columns":
                [
                    "User",
                    "CompanyId"


                ], // Specify the list of columns to take from the reference table.
            "Filters": // Filter options in case they are needed.
                [
                    {
                        "Column": "IsPrimary",
                        "ParamName": "IsPrimary",
                        "Value": isPrime
                    }
                ]
        }];


    var CompanyId;
    var users;
    Database.readCompound("rodent", "UserRoles", userFilter, (eventData) => {

        var userSensa = SensaCollection.load(eventData.value);
        CompanyId = userSensa.getColumn("CompanyId");
        users = userSensa.getColumn("User")
        var userNameData = [];

        var userIndex = users.indexOf(user);

        companyId = CompanyId[userIndex];

        for(var i = 0; i < CompanyId.length; i++) {
            if (CompanyId[i] == companyId) {
                //var index = 
                userNameData.push(users[i]);
            }
        }
        userNameData = [...new Set(userNameData)];
        //debugger;
        Database.readRecords("Directory", "users", (data)=>{
            var sensaData = SensaCollection.load(data.value);

            var aliasData = [];

            var username = sensaData.getColumn("username");
            var alias = sensaData.getColumn("alias");
            var status = sensaData.getColumn("status");

            for (var i = 0; i < username.length; i++) {
                for (var j = 0; j < userNameData.length; j++) {
                    if (username[i] == userNameData[j] && status[i] == 1) {
                        aliasData.push(alias[i]);
                    }
                }
            }
            loadAllUsersFromCompany();
            //debugger;
        })

    });
}

