// constants **********************************************

const deviceTable = Script.getWidget("deleteDevicesTable");
const modelTypeDrop = Script.getWidget("deleteModelTypeDrop");
var modelTypeCollection;
var modelCollection;


/**
 * Initialise script state (run once at startup) **********************************************
 */
Script.on('load', function() {
	loadModelTypes();
	loadDeviceTableParam("all");
});



// Client Event Subscriptions **********************************************

ClientEvents.subscribe("filterDevices", filterDevices);
ClientEvents.subscribe("deleteDevices", deleteDevices);





// functions **********************************************

function filterDevices() {
	deviceTable.deleteAllRows();
	var modelType = Script.getFormByKey("drop").modelType;
	console.log("model type " + modelType);
	loadDeviceTableParam(modelType);
}

function deleteDevices() {
	// get the selected devices from the table
	var deviceCollection = SensaCollection.load(Script.getFormByKey("deviceTable").selectedDevices);
	console.log(JSON.stringify(deviceCollection,null,4));

	// delete them one by one, or try to add them to one collection to delete them all at once.
	deviceCollection.forEach(function(row,pk) {
		console.log("pk " + pk);
		Database.deleteRecord("rodent", "Devices", "Id", pk);
	});
}

function loadDeviceTable(modelType) {
	var filter = {filter: `ModelId=${modelType}`, columns: "Id, DeviceNumber"};

	// remove the filter if all devices are selected
	if (modelType == "all") {
		delete filter.filter;
	}

	Database.readRecords("rodent", "Devices", function (eventData) {
		var collection = SensaCollection.load(eventData.value);
		console.log(JSON.stringify(collection,null,4));
		deviceTable.receiveValue(collection);
	}, filter);
}

function loadDeviceTableParam(modelType) {
	// read records param
	var selectStatement = {Sql: "SELECT Devices.Id, Devices.DeviceNumber, Model.ModelType FROM Devices JOIN Model ON Devices.ModelId=Model.Id WHERE Model.ModelType="+modelType, Data: []};

	if (modelType == 'all') {
		selectStatement = {Sql: "SELECT Devices.Id, Devices.DeviceNumber, Model.ModelType FROM Devices JOIN Model ON Devices.ModelId=Model.Id", Data: []};
	}

	console.log(JSON.stringify(selectStatement,null,4));

	Database.ReadRecordsParam("rodent", "Model", [selectStatement], function (dbResponse) {
		console.log(JSON.stringify(dbResponse,null,4));
		console.log("We in the finction");
		var collection = dbResponse.value;
		collection.pk = "Id";
		collection = SensaCollection.load(collection);
		deviceTable.receiveValue(collection);
	});
}



function loadModelTypes () {
	var filter = {columns: "Id, Name"};
	Database.readRecords("rodent", "ModelType", function (eventData) {
		modelTypeCollection = SensaCollection.load(eventData.value);
		console.log(JSON.stringify(modelTypeCollection,null,4));
		modelTypeCollection.renameColumn("Id", "value");
		modelTypeCollection.renameColumn("Name", "text");
		modelTypeCollection.add({text: "All Devices", value: "all"});
		modelTypeDrop.receiveTextValues(modelTypeCollection);
		modelTypeDrop.receiveValue("All Devices");
	}, filter);
}

function loadModels() {
	var filter = {columns: "Id, ModelType"};
	Database.readRecords("rodent","Model", function(dbResponse) {
		modelCollection = SensaCollection.load(dbResponse.value);
		console.log(JSON.stringify(modelCollection,null,4));

	}, filter);

}
